(window.webpackJsonp=window.webpackJsonp||[]).push([[24],{223:function(v,_,n){"use strict";n.r(_);var r=n(28),b=Object(r.a)({},(function(){var v=this,_=v.$createElement,n=v._self._c||_;return n("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[n("p",[v._v("校庭に石灰で魔法陣を書き、俺の持っていたお守りを触媒にし、四人で手を繋いで異世界転移の呪文を唱えたところ、魔法陣が強い光を放った。同時にものすごい風が吹いてきて、四人で必死になってお互い手を離さないように気をつけるんだけどいつまで経っても風が止まなくてこれなんかおかしくね？　風吹いてる時間長くね？　と思って頑張って目を開けてみたらめちゃめちゃ上空にいてビビる。眼下にどこかの町が見え、小さかった屋根がぐんぐん近付いてきてあっやばいこれ超落ちてる。この高さから落ちたら死ぬ。でも自力でなんとかできる種類のものじゃないしさすがの秋元とはいえ魔法陣なしになんかできるわけじゃないだろうしこの世界でこのタイミングで助けてくれそうな誰かっていうとひとりしか思いつかないわけで俺はその名前を全力で呼ぶ。")]),v._v(" "),n("p",[v._v("「リュグナーーーーーーーー助けてーーーーーーーーーー！！！！！」")]),v._v(" "),n("p",[v._v("瞬間、体が何かに引っかかったように減速した。イメージ的には雲を突き抜けるような、現実の雲ではなくアニメの微妙に質量のある雲を突き抜けるような感覚が何度かあって、最終的に高飛びで使うマットくらいのもの（見えない）に一旦着地した後、十五センチくらいの高さからまたちょっと落ちた。結果的にちょっと打った膝が痛いくらいで済んだ。なるほど上下方向に座標がずれることは想定してなかった。"),n("br"),v._v("\n　落ちた場所は街のちょっと横くらいの、見慣れた懐かしいサバンナだった。今日も草木が青々としている。"),n("br"),v._v("\n「っっっっっっあ、すっげビビった、はーやばい死ぬかと思った、全員無事？　怪我ない？」"),n("br"),v._v("\n「大、丈夫、生きてる」"),n("br"),v._v("\n「びっくりした……今のなに？　なんか、見えないクッションみたいなのにぶつかったような」"),n("br"),v._v("\n　望月さんとローラが口々に感想を述べる。何も起きないか無事に転移するかの二択だと思ってたら急に上空に放り出されたわけだからそりゃびっくりもするわ。"),n("br"),v._v("\n「菅原、今リュグナって言ったか？　リュグナってまさかあのリュグナ様か？」"),n("br"),v._v("\n　一足先に状況を飲み込んだらしい（すげえ）秋元が、肩で息をしながらこちらを向く。顔が青い。"),n("br"),v._v("\n「あ、そうか秋元は知ってるのか？　たぶんそのリュグナだ。伝説みたいなものだっては聞いてる」"),n("br"),v._v("\n「みたいなものっていうか、完全に伝説だろ？！　生きてるのか？！」"),n("br"),v._v("\n「うん。確かあれ、時間の止まった……塔？　外観見てないからよくわかんないけど、建物に」"),n("br"),v._v("\n「外観見てないって、中には入ったってことか？！」"),n("br"),v._v("\n「うん、あの、呼ばれて」"),n("br"),v._v("\n「呼ばれた？！」食いつきっぷりがすごい。びっくりする。常識人かと思ったら常識ゴリラ側なのか秋元？"),n("br"),v._v("\n「待て、どういう――なんでそんなことができる？　リュグナ様の居た『塔』って、つまり」"),n("br"),v._v("\n「あのごめんあのね詳しくは訊かれてもよくわかんないっていうかですね、俺の仲間の話だと、時間を止めた塔にずっと引きこもってるって話でして、俺としてもちょっとお呼ばれしたくらいの気分しかなくて」"),n("br"),v._v("\n「引きこもってる？」秋元の勢いが弱まる。「……俺の知ってる話と違う」"),n("br"),v._v("\n「違う？」"),n("br"),v._v("\n「リュグナ様は閉じこもってるんじゃない。閉じ込められたんだ、この国の人々の手で」"),n("br"),v._v("\n「どういうこと？」っていうかやっぱり「人々」って言うよね？　ゴリラゴリラとか言わないよね？　俺だけじゃないよね？"),n("br"),v._v("\n「昔――この国がまだ無かった頃、後に初代の王となるがリュグナ様の力を頼った。リュグナ様は彼に請われて国の土台を作った。陣を敷き、結界を張り、人々が安全に暮らすための礎を作ったんだ。国ができて人々は喜び、同時に恐れた。『作れるものは壊すこともできる』と。それで国中の魔道士たちが結託し、リュグナ様をあの『塔』に閉じ込めた。外界に対する干渉はかなわない、時間すらも干渉しないあの場所に。いつでもまた使えるように、生かしたままで。だからリュグナ様が生きているなら出てくるなんてことはありえない。出られたなら生きているはずがない」"),n("br"),v._v("\n「……ふつうに出てきたんだけど」"),n("br"),v._v("\n「出てきたって？」"),n("br"),v._v("\n「呼んだら、こう――瞬間移動みたいな、ぶわーって」だめだ語彙力がついてこない。っていうかわかんない。何が起きてたんだあの時。"),n("br"),v._v("\n「……なるほど、『閉じこもってる』ってそういう意味か……」"),n("br"),v._v("\n　秋元が力のない声で吐き出しながら、顔を覆って座り込む。情緒安定してなくない？　大丈夫？"),n("br"),v._v("\n「リュグナ様は本当にただ自らあの塔に引きこもってたんだ。人々の恐れと無責任な依存を両方わかった上で、生かされてくれていた」"),n("br"),v._v("\n「なるほど」"),n("br"),v._v("\n「気が遠くなる……我々はどんな大罪を……」"),n("br"),v._v("\n　秋元が顔を覆ってうなだれる。うーん、そこだけ切り取るとものすごい聖人のような気がするけど本人がアレだからなあ。なんか別の意図があるような気がしてならない。それもそこそこ不遜で悪趣味なやつ。"),n("br"),v._v("\n「ちなみに話したいなら呼んでみるけど」"),n("br"),v._v("\n「いや、今はいいや。ちょっと整理がつかない」"),n("br"),v._v("\n「私ら全然ついていけてないんだけどなんかものすごい偉い人に菅原くんがサクッと会ったことあって秋元くんがびっくりしてるって認識でいい？」"),n("br"),v._v("\n「大体合ってる」"),n("br"),v._v("\n「そんでさっき助けてくれたのがその人っていうこと？」"),n("br"),v._v("\n「そう」どうでもいいけどものすごい理解早いな望月さん。話が早くて助かる。"),n("br"),v._v("\n「リュグナー。助かったよーありがとうー」"),n("br"),v._v("\n　やっぱり虚空に向けて声を出す以外のテレパシー起動方法はわからないので虚空に向かって声を出す。")]),v._v(" "),n("p",[v._v("――どういたしまして。すまないが今ちょっと忙しいんだ、あとで会いにいくよ。")]),v._v(" "),n("p",[v._v("「了解ー」変わりないようで何より。でもたぶんあいつ何百年単位で特に変わりないんだろうな、秋元の話を聞いた限り。"),n("br"),v._v("\n「……それテレパシー？　なの？」"),n("br"),v._v("\n　望月さんが怪訝な顔をしている。"),n("br"),v._v("\n「あーうん、声を出さずに伝えるコツがわかってない。望月さん、リュグナの返事も聞こえてない？」"),n("br"),v._v("\n「何も聞こえなかった」"),n("br"),v._v("\n「そりゃ変に見えるわ……パッと見ひとり言じゃん」"),n("br"),v._v("\n「ローラはどうしてるの？　テレパシーにコツとかある？」"),n("br"),v._v("\n「どうって言われても……ううんごめん、感覚的なものだから、言葉にするのは難しいかも」"),n("br"),v._v("\n「そっか」"),n("br"),v._v("\n「念じれば届くのかもしれないけど、脳内ダダ漏れかと思うとそれちょっと怖いんだよね俺。ローラ、どう？　俺の考えダダ漏れたりしてない？」"),n("br"),v._v("\n「特に何も」"),n("br"),v._v("\n「よかった」"),n("br"),v._v("\n「で、ここからどうするの？　現在位置わかんなくない？」"),n("br"),v._v("\n「取り敢えずそっちに街が見えるから、あそこに行ってみよう。誰かが助けてくれるかもしれない」"),n("br"),v._v("\n「誰かって、誰が？」"),n("br"),v._v("\n「誰かは誰かだ。ここがどこかもわからないのにそれが誰かなんてわかりようがない」なんか言ってて自分でよくわかんないことになってるけど。"),n("br"),v._v("\n「何かアテがあるのか？」"),n("br"),v._v("\n「まあ、大丈夫」"),n("br"),v._v("\n　ゴリラ優しいし。")]),v._v(" "),n("p",[v._v("近くに見えた街に入ろうとすると、ものすごい勢いですっ飛んできたゴリラ三人に囲まれてしまった。腕章を付けているから、たぶんこの街かこの近くに常駐している駐屯兵だろう。"),n("br"),v._v("\n「こんにちは」"),n("br"),v._v("\n　おそらく不審な侵入者として対応するつもりだったのだろうゴリラたちは、俺を見るなり「え、――あれ？　勇者さまでは？」と声を上げた。話が早くて助かるけど個体の識別とかできてんのかな。特に俺と秋元、背格好もカラーリングも"),n("br"),v._v("\n「どうしてこのようなところに。お帰りになったと伺っておりましたが」"),n("br"),v._v("\n「ええと、話せば長くなるんですけど一旦帰ってまた来たという感じで」"),n("br"),v._v("\n「そちらはお連れ様ですか？」"),n("br"),v._v("\n「はい。怪しいものではありません」"),n("br"),v._v("\n「菅原くん」"),n("br"),v._v("\n　駐屯兵と話していると、望月さんに後ろから服の裾をつんつんと引かれた。なにそれ可愛い。"),n("br"),v._v("\n「なに？」"),n("br"),v._v("\n「何て言ってるの、ゴリラたち」"),n("br"),v._v("\n「え？　あれ、わかんない？　菅原も？」"),n("br"),v._v("\n「わからない」"),n("br"),v._v("\n「マジか」"),n("br"),v._v("\n　ローラは俺が「わかる？」と訊く前に、視線を向けただけで首を振った。え、ローラもわかんないのか。ということは望月と秋元がローラの言葉を理解していたわけではなく、ローラがそもそも日本語喋っちゃってたってことか。しかもローラはこの世界のゴリラ語はわからないと。マジか。"),n("br"),v._v("\n「どうかされましたか？」"),n("br"),v._v("\n「あーえっと、彼ら、言葉がわからないらしくて」"),n("br"),v._v("\n「ああ。それでは礼拝堂へご案内します。詳しいお話もそちらでお聞かせください」"),n("br"),v._v("\n「礼拝堂？」"),n("br"),v._v("\n「はい。勇者さまも前回こちらへいらしたときにご覧になったでしょう？」"),n("br"),v._v("\n　あ、あれか。あの黒魔術みたいな怪しい場所。礼拝って言葉から随分遠い気がするんだけどあれ。俺のイメージしてる礼拝ってもっとなんかステンドグラスとか赤い絨毯とかパイプオルガンとか――まあいいや。たぶんこのイメージもドラクエをベースにいろいろ混ざった似非宗教観だろうし。"),n("br"),v._v("\n「案内してくれるって。ついて来て」"),n("br"),v._v("\n「案内って、どこに？」"),n("br"),v._v("\n「礼拝堂。前に俺が掛けてもらった翻訳魔法を三人にも掛けてもらう。そしたら三人も言葉がわかるようになるはず」"),n("br"),v._v("\n「慣れてるな」"),n("br"),v._v("\n「まあ一回来てるからね」"),n("br"),v._v("\n「美咲、さっきからキョロキョロしすぎ」"),n("br"),v._v("\n「だって右も左もゴリラだらけなんだよ……？！　すごい、本当にゴリラの文明だ……！！」"),n("br"),v._v("\n　望月さんはまだ小声だが、その声には興奮がたしかに滲んでいる。ローラの腕に抱きつくように体を寄せながら足元だけぴょこぴょこと跳ねている。楽しそうで何よりだけどこれ言葉がわかるようになったとして制御しきれるのか俺ら。秋元にそう尋ねると、秋元は望月さんと俺とを交互に見た後で「……頑張るしかない」とコメントを返した。弱気。")]),v._v(" "),n("p",[v._v("礼拝堂に入ると、俺は三人とは別室に通された。審問っぽくてちょっと緊張する。"),n("br"),v._v("\n「いえ、そんな大仰なものではありません。我々は勇者さまとお話してみたかっただけです。翻訳も必要ありませんし」"),n("br"),v._v("\n　そうは言ってもゴリラ三対俺という空間はなんか結構な圧迫感がある。ゴリラサイズの椅子は低いから気分的には楽なんだけど。"),n("br"),v._v("\n「そんなに時間かかるもの？　俺のときは一瞬だった気がするんだけど」"),n("br"),v._v("\n「それは予め陣を敷いていましたから。今からすべて準備するとなると、三十分ほどはかかります」"),n("br"),v._v("\n　なるほど。急に来てごめんかった。"),n("br"),v._v("\n「勇者さまはどうしてまたこちらに？　何か我々の聞き及んでいない問題でも発生したのでしょうか？」"),n("br"),v._v("\n「いや、今回は呼ばれて来たわけじゃないんだ。さっき俺と一緒にいた子、」あれ？　そういえばこっちの世界で俺たちとゴリラってどう区別すればいいんだ？　前回はゴリラしかいなかったからだいたい「人」って言葉で誤魔化して喋ってたけどどう表現すればいいのこれ？"),n("br"),v._v("\n「勇者さま、どうかなさいましたか？」"),n("br"),v._v("\n「え？　あ、ごめんえっと、俺と一緒に来た背の高い方の女の子わかる？」"),n("br"),v._v("\n「女の子――ええ、はい」"),n("br"),v._v("\n　言いながらだいぶ微妙な顔をする。これさては性別がわかってねえなって思う。いや俺もゴリラの性別とかパッと見じゃわかんないから仕方ないけど。"),n("br"),v._v("\n「紹介はあとでするとして、その背の高い方の子が、どうやらこっちの世界から来たんじゃないかという話になって」"),n("br"),v._v("\n「さて……そういう話は聞いた覚えがありませんが……」"),n("br"),v._v("\n「そっか。あっちで話してても埒が明かないと思って来てみたんだけど。こういう話って誰に聞くのが一番いいんだろう？」"),n("br"),v._v("\n「そういうことであれば我々も助力いたしましょう。人手は多い方がいい」"),n("br"),v._v("\n　どうでもいいんだけどゴリラが胸を叩いて「任せろ」みたいな素振りをするとなんか頼れる感がすごい。胸の厚み。拳の大きさ。超頼れそう。"),n("br"),v._v("\n「いや、ただでさえ急に来たのにそこまでお世話になるのはさすがに」"),n("br"),v._v("\n「いえいえ大丈夫です。何しろ世界を救っていただいたのですから、勇者さまの力になれるとあれば皆競って力を貸してくれるはずです」"),n("br"),v._v("\n　うーんやはりゴリラはいいやつらだ。ありがとうゴリラ。持ちつ持たれつの関係っていいよな。持ちつ持たれつっていうか、担がれ運ばれてた記憶の方が多いけど。"),n("br"),v._v("\n「そういうことなら、じゃあ、お世話になります。こっちの復興状態はどう？　散らかしっぱなしで帰っちゃったからちょっと心配してたんだけど」"),n("br"),v._v("\n「そのあたりはご心配に及びません。一同尽力しましたし、あのリュグナ様も手を貸してくださいましたから。目を瞠るような速度でしたよ」"),n("br"),v._v("\n　ああ。もしかしてさっきの「ちょっと忙しい」ってその話か？　まあ使えるものが適切に使われてるのはよいことだ。本人も面目躍如ができて本望だろう。"),n("br"),v._v("\n「国交は大丈夫だった？　偽王が随分荒らしてたと思うんだけど」"),n("br"),v._v("\n「そのあたりも一応、説明してご了解をいただきました。もちろん、応援依頼を断った国々の犠牲者たちは謝って戻ってくるものではありませんが、勇者さまのご活躍もお借りしてトントンというところです」"),n("br"),v._v("\n　すごいな。そんなサクッと溜飲を下げてくれるもんだろうか。いくら偽王が好き勝手してたとはいえ魔王が攻めてきたのに協力を拒んで専守防衛って、結構ガチで戦争になりかねないっていうか国交断絶レベルの話だったと思うんだけど。ゴリラ界ちょっと寛大すぎるのでは？　っていう話を秋元にしたところ、秋元が「それはこの国――というかこの世界の宗教の話だ」と言ってまあ長い長い話を始めてしまい、疲れた。"),n("br"),v._v("\n「それはねえ、秋元くんにその手の話を振ったのが悪いよ」"),n("br"),v._v("\n「秋元あれなの、歴ヲタ？」"),n("br"),v._v("\n「そうそう。だからダメだよ、迂闊にその手の話を振っちゃあ」"),n("br"),v._v("\n　望月さんがニヤニヤしながら秋元を見、秋元は「菅原が知りたがってたから説明しただけで」とぶつぶつ釈明する。ローラはそんな二人を見てくすくす笑う。和やかだ。"),n("br"),v._v("\n　三人に対しての翻訳魔法は無事に終了したらしく、望月さんは礼拝堂の中を歩き回ってはあれは何だこれは何だと魔道士たちに話しかけている。ストッパーたる秋元はというとこちらも調度品を眺めるのに夢中になっていて、俺はローラと二人でなんとなく取り残されてしまう。"),n("br"),v._v("\n「……あれは、あれでいいの？」ローラが不安そうに訊いてくる。"),n("br"),v._v("\n「……今のところストップ入ってないし……？」俺もよくわからないのでぼそぼそ返事をする。おいそこの歴ヲタ。お前がそっち側に行ってどうする。")]),v._v(" "),n("p",[v._v("っていうかツッコミ役いないのここ？")]),v._v(" "),n("p",[v._v("滞在する宿とかなんとかかんとかの手続きはまるっと引き受けてくれるとのことで、俺たち四人は「観光でもなさってください」との厚意によりぽいっと街に放り出されてしまった。相変わらず丁重なのか雑なのかよくわからない扱い。別に不満は無いんだけど。"),n("br"),v._v("\n　そういうわけで俺は未だにキョロキョロしてる秋元と別の意味でキョロキョロしてる望月さんと望月さんにべったり絡みつかれてるローラを引き連れて街へ出る羽目になったのだった。なんというか、前回はそこそこ、ある程度の問題があったとはいえ常識人パーティだったから多少楽だったけど、今回は本当にツッコミ不在って感じだ。たぶん誰一人として非常識のレベルではないんだけど微妙に収拾がつかないというか。"),n("br"),v._v("\n「すごいすごいすごいすごい！　本当にどこもかしこもゴリラだらけ！！　あっねえねえそこのお店見てきていい？　服屋さんっぽいのがある！」"),n("br"),v._v("\n　言いながら望月さんはローラの腕を掴んだままふらふら～っとそちらの方へ歩いていってしまい、俺と秋元は慌ててその後を追う。行動する前に申告してくれるのはありがたい。"),n("br"),v._v("\n「そういえばここさあ、服着てるゴリラと着てないゴリラいるじゃん？　どういう基準？」"),n("br"),v._v("\n　前回から気になっていたことを訊くと、秋元は「人間の着る服みたいに肌を隠す種類のものじゃないんだ」と説明を始めてくれる。「それは体毛があるから。ただ、雨の日に体が濡れないようにとか、職業のシンボルであるとか、あるいは単なる装飾のためだとかで着るものは無いわけじゃない。俺らの感覚で言うところのアウターに近いものだと思えばだいたい正しい」"),n("br"),v._v("\n「なるほど」"),n("br"),v._v("\n　いやいいわ解説役。便利だな。前回は周りにゴリラしかいなかったからそこの比較検討はしにくかったんだ。質問の意図が明確に伝わるってイイ。あと同種同世代同性の安心感。今更ゴリラを敵だなんて全く思っちゃいないけど問答無用の圧倒的な安心感がある。精神的にすごく助かる。"),n("br"),v._v("\n「ねえ見て見て、ローラとお揃いのミサンガもらっちゃった」"),n("br"),v._v("\n　ほくほく顔で戻ってきた望月さんの手首には、ローラの手首に巻かれているものと同じミサンガが巻かれていた。ローラは一周、望月さんは二周。なるほど便利だ。"),n("br"),v._v("\n「もらったって、どういう」"),n("br"),v._v("\n「可愛い子にはサービスだって。見る目あるよねあのおじさん」"),n("br"),v._v("\n　それはどっちの意味なんだろうかおじさん。いや望月さん的にはおそらく可愛いのはローラだと認識しているだろうが。単にキャイキャイした女の子ふたりが来たから適当にサービスしただけだろうか。"),n("br"),v._v("\n　望月さんのテンションの上がりようは割と予想の範疇というか、そもそも常識人なので勝手に居なくなったりはせずぴょこぴょこ跳ねながらきゃあきゃあ言ってる程度なのだけど、秋元は無言で足を止める。地面とか壁とか何が面白いのかよくわからないが止まるなら止まるでなんか言ってくれ。"),n("br"),v._v("\n　そんなだから結果的に俺はずんずん進んで行っちゃう望月さん＆ローラ組と気付いたら足を止めてなんか見てる秋元の間でひとりおろおろしてることになる。自由かよ。"),n("br"),v._v("\n「ねえねえあれは何？　あの緑の建物」"),n("br"),v._v("\n「み゛っ」変な声が出た。ごほん。「あー……キャバクラ、てきな」"),n("br"),v._v("\n「なるほど。道理であの辺りだけ美男美女が多いと思った」"),n("br"),v._v("\n　ゴリラに対するその造詣の深さ何？　英才教育？　望月家はそうなの？　ゴリラの美醜見分けられるっておかしくね？"),n("br"),v._v("\n「別になんてことないでしょ。美醜なんて突き詰めれば個人の好みの話じゃん」"),n("br"),v._v("\n「とは言ってもなあ……体が大きいとかっこいいみたいなのはあるけど……」"),n("br"),v._v("\n「一緒だよ一緒」"),n("br"),v._v("\n「そうかなあ……」"),n("br"),v._v("\n　なんかちょっと釈然としないものがあるんだけどそれはもしかしたら俺の中にゴリライコール強いかっこいいしか存在しないせいかもしれない。っていうかそれ以前にまず雌雄の見分けがついてない。でかくて強そうなのが必然的に雄に見えちゃうけどどうなんだろうか実際。")]),v._v(" "),n("p",[v._v("ゴリラ界は日が傾き始めている。季節はおよそ春か秋という感じで、暑くなく寒くない。前回来たときもこんな感じだったので、季節がないかもしくは周期がだいたい日本と似ているんだろう。一日の長さはというと、人の体内時計とは適当なもので、日が昇って落ちれば一日という感覚に変わりはなく、前回は長いとも短いとも思わなかった。今回はスマホがあるので、電波こそ入らないが時計としては機能する。現在十六時半。"),n("br"),v._v("\n「ところでローラ、どうかした？」"),n("br"),v._v("\n　望月さんに急に水を向けられて、ローラが「えっ？」と高い声をだす。裏返っちゃったやつだ。聞いてなかったことにしよう。"),n("br"),v._v("\n「あ、ごめん聞いてなかった。何？」"),n("br"),v._v("\n「なんかぼうっとしてるみたいだから。疲れちゃった？」"),n("br"),v._v("\n「うん、ちょっとだけ。……こんな街、初めてだから」どきどきしちゃって、と続く声はゆるゆると小さくなり、雑踏の喧騒に紛れた。"),n("br"),v._v("\n「え～～～～～～～～かわいい～～～～～～～！！！！！！！！」"),n("br"),v._v("\n　望月がきゃあきゃあ言いながらローラに抱きつく。ローラもきゃあきゃあ言いながら望月を抱き返す。秋元が微笑ましそうに二人を眺めているんだけどやっぱりツッコミ不在なのこれ？"),n("br"),v._v("\n「ごめん失礼な質問かもしれないんだけど気を悪くしないでほしいんだけど微笑ましいみたいな顔で見てるけどあれは微笑ましいってカテゴリでいいの」"),n("br"),v._v("\n「そうだよ」"),n("br"),v._v("\n　そうなの？"),n("br"),v._v("\n「そうだよ。だってあれはエーヴィヒ姫がかつて望んだ光景そのものだから」秋元が笑顔を作って俺を見る。「姿形も文化も何もかもが違う二者間であれだけの信頼、親愛がつくられてる。それが喜ばしい以外に何だと思う？」"),n("br"),v._v("\n　訊かれて初めて、自分の中の差別感情を目の前に引きずり出されたような気分になった。もちろんそんなつもりじゃなかった。でも態度としてはそれ以外の何でもなかった。ああそうか、差別したのか今。"),n("br"),v._v("\n「……ごめん、俺が悪かった」"),n("br"),v._v("\n　俺が謝ると、秋元は小さく喉を鳴らして笑った。"),n("br"),v._v("\n「怒ってるわけじゃない。実際、望月の適応速度は異常だと思う。丸一日くらいはかかると思ってた」"),n("br"),v._v("\n「いや丸一日でもかなり早いと思うけど」"),n("br"),v._v("\n　しかしローラ、マジでちょっと元気が無さそうに見える。確かに向こうの世界で街に出る機会は無かったんだろうが、単なる緊張で片付けていいもんだろうか。"),n("br"),v._v("\n「疲れたのかもしれないから、早めに休もう。宿は用意してくれるみたいだし」"),n("br"),v._v("\n「そうだね、ローラに無理させたくないし」"),n("br"),v._v("\n「え、いいよ悪い、美咲も秋元くんもすごい楽しそうなのに」"),n("br"),v._v("\n「ローラが一緒じゃなきゃ楽しくないもん。今日は休んで、明日また一緒に回ろ」"),n("br"),v._v("\n「……うん、ありがとう」"),n("br"),v._v("\n　だいたい話もまとまったところ悪いんだけど今日の宿はまだわからない状態で、これはたぶん誰かに問い合わせなきゃいけないもので、そのためには礼拝堂に戻るのが最短ルートなんだろう。テレパシー、ピンポイントで相手を選びたいとまで言わないけど方角と距離くらい決められたらいいんだけどなあ。魔王戦の時はすっげえ役に立ったけど日常レベルでは役に立たないんだなこれ。")]),v._v(" "),n("p",[v._v("ローラに気遣いながら来た道を戻り、礼拝堂の手前で腕章をつけたゴリラに会った。状況を説明すると宿は取ってくれたとのことで、そこからまた十五分くらい歩いて宿へ向かう。"),n("br"),v._v("\n　取ってもらった部屋は大きめの広間だった。大きめというか、ゴリラ的にはツインルームくらいのものなのかもしれない。わかんないけど。曰く、街にちょうど旅の商隊が来ていて、四人いっぺんに泊まれるような場所が他に無かったのだそうだ。"),n("br"),v._v("\n「っていうかさあ、高校生にもなって男女同室ってどうなの実際」"),n("br"),v._v("\n　ゴリラベッドは相変わらずゴワッゴワのガッサガサで、寝心地がいいとは言えない。"),n("br"),v._v("\n「一応言っておくと俺、空手と合気道と柔道の段位持ってるからそのつもりでよろしく」"),n("br"),v._v("\n　警戒がガチじゃん。何もしねえよ睨まないでくれよ。つうかそっちの意味じゃねえし。"),n("br"),v._v("\n「あれ、秋元くんって弓道部とかじゃなかった？」"),n("br"),v._v("\n「部活とは別にやってる」"),n("br"),v._v("\n　遠距離戦もいけんのかよなにそれ強い。つうか羨ましい。俺もなんか戦闘ステータスあれば前回もっと楽だったかもしれないんだけどなあ。楽っつうか、なんかできたかもしれないっつうか。"),n("br"),v._v("\n　俺がちょっといろいろ思い出してくさくさしている間にも望月さんは上機嫌で「へへへー、なんか合宿みたいー」とにこにこしていてすごい。カルチャーショック皆無。"),n("br"),v._v("\n「この前の修学旅行とかローラと一緒に行けなかったしさあ、なんか嬉しくなっちゃうなあ」"),n("br"),v._v("\n「ローラは留守番だったんだ？」"),n("br"),v._v("\n「そりゃあ、ゴリラは旅客機に乗れないし」"),n("br"),v._v("\n　あ、そこは現実的な話なんだ。よくわかんねえな望月さんの世界観。"),n("br"),v._v("\n「菅原」"),n("br"),v._v("\n「ん、何？」"),n("br"),v._v("\n「電気――じゃないな、灯り、これどうやったら消えんの」"),n("br"),v._v("\n「そこにスイッチがあるじゃろ？」"),n("br"),v._v("\n「……なんでここだけ向こうと同じなんだ？」"),n("br"),v._v("\n　俺に訊かれても知らん。")])])}),[],!1,null,null,null);_.default=b.exports}}]);